import { Component } from "react";
import { MyLoader } from "./MyLoader";

export class App extends Component {
  state = {
    pokemon: "",
  };

  handleInputChange = (event) => {
    this.setState({ pokemon: event.currentTarget.value });
  };

  render() {
    return (
      <>
        <MyLoader />
        <form>
          <input type="text" value={this.state.pokemon} onChange={this.handleInputChange} />
          <button type="submit">Search</button>
        </form>
      </>
    );
  }
}
