import axios from 'axios';

axios.defaults.headers.common['Authorization'] = 'Bearer 4330ebfabc654a6992c2aa792f3173a3';

export const APIfetchArticles = ({ searchQuery = '', currentPage = 1, pageSize = 5 }) => {
  return axios
    .get(
      `https://newsapi.org/v2/everything?q=${searchQuery}&pageSize=${pageSize}&page=${currentPage}`
    )
    .then((response) => response.data.articles);
};
