import { Component } from "react";
import todos from "../../../json/todos.json";
import { List } from "../List";

export class TodoList extends Component {
  state = {
    todos,
  };
  deleteTodo = (id) => {
    this.setState((prevState) => ({
      todos: prevState.todos.filter((todo) => todo.id !== id),
    }));
  };
  render() {
    const { todos } = this.state;
    // const completed = todos.filter((todo) => todo.completed);
    const completed = todos.reduce((acc, todo) => {
      if (todo.completed) {
        acc + 1;
      }
    }, 0);
    return (
      <>
        <div>
          <p>Общее количество Todo: {todos.length}</p>
          <p>Количество выполненых: {completed.length}</p>
        </div>
        <List todos={todos} title="Состояние Компонента: " onDelete={this.deleteTodo} />;
      </>
    );
  }
}
