import { Component } from "react";
import axios from "axios";
import { Loading } from "components/Loading";
import { ModalError } from "components/ModalError";
import { Modal } from "react-bootstrap";

export class PokemonInfo extends Component {
  state = {
    pokemonCustom: null,
    loading: false,
    error: null,
  };
  componentDidUpdate(prevProps, _) {
    const prevName = prevProps.pokemonName;
    const currName = this.props.pokemonName;
    if (currName !== prevName) {
      this.setState({ loading: true });

      axios
        .get(`https://pokeapi.co/api/v2/pokemon/${currName}`)
        .then((pokemon) => {
          console.log(pokemon);
          this.setState({ pokemonCustom: pokemon });
        })
        .catch((error) => this.setState({ error }))
        .finally(() => this.setState({ loading: false }));
    }
  }

  render() {
    const { loading, pokemonCustom, error } = this.state;
    const { pokemonName } = this.props;
    return (
      <div>
        {error && (
          <ModalError>
            <h3>–≠–π, –¥—Ä—É–≥, –º–Ω–µ –∫–∞–∂–µ—Ç—Å—è —Ç—ã –¥–µ–ª–∞–µ—à—å —á—Ç–æ-—Ç–æ –Ω–µ —Ç–∞–∫!</h3>
          </ModalError>
        )}
        {loading && <Loading />}
        {pokemonName === "" && <div>–Ø –∂–¥—É –ø–æ–∫–∞ —Ç—ã –Ω–∞—á–Ω–µ—à—å –º–µ–Ω—è –∏—Å–∫–∞—Ç—åüôà</div>}
        {pokemonCustom && (
          <div>
            <img
              src={pokemonCustom.data.sprites.other["official-artwork"].front_default}
              width="300px"
              alt="pokemon"
            />
            <h2>{pokemonCustom.data.name}</h2>
          </div>
        )}
      </div>
    );
  }
}
