import { Component } from "react";

export class Form extends Component {
  state = {
    name: "",
    email: "",
    experience: "junior",
  };

  handleChangeEvent = (event) => {
    const { name, value } = event.currentTarget;
    this.setState({
      [name]: value,
    });
  };

  handleSubmit = (event) => {
    event.preventDefault();

    this.props.onSubmit(this.state);
    this.reset();
  };

  reset = () => {
    this.setState({ name: "", email: "" });
  };
  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <label>
          <input
            type="text"
            name="name"
            placeholder="name"
            onChange={this.handleChangeEvent}
            value={this.state.name}
          />
        </label>

        <label>
          <input
            type="text"
            name="email"
            placeholder="email"
            onChange={this.handleChangeEvent}
            value={this.state.email}
          />
        </label>
        <p>Ваш уровень разработчика: </p>
        <label>
          Junior
          <input
            type="radio"
            value="junior"
            name="experience"
            onChange={this.handleChangeEvent}
            checked={this.state.experience === "junior"}
          />
        </label>
        <label>
          Middle
          <input type="radio" value="middle" name="experience" onChange={this.handleChangeEvent} />
        </label>
        <label>
          Senior
          <input type="radio" value="senior" name="experience" onChange={this.handleChangeEvent} />
        </label>
        <button type="submit">Send</button>
      </form>
    );
  }
}
