import { Component } from "react";

export class Form extends Component {
  state = {
    name: "",
    email: "",
  };

  handleChangeEvent = (event) => {
    const { name, value } = event.currentTarget;
    this.setState({
      [name]: value,
    });
  };
  render() {
    return (
      <form>
        <label>
          <input
            type="text"
            name="name"
            placeholder="name"
            onChange={this.handleChangeEvent}
            value={this.state.name}
          />
        </label>

        <label>
          <input
            type="text"
            name="email"
            placeholder="email"
            onChange={this.handleChangeEvent}
            value={this.state.email}
          />
        </label>
      </form>
    );
  }
}
