      return <div>–Ø –∂–¥—É –ø–æ–∫–∞ —Ç—ã –Ω–∞—á–Ω–µ—à—å –º–µ–Ω—è –∏—Å–∫–∞—Ç—åüôà</div>;
    }
    if (status === "pending") {
      return <Loading />;
    }

    if (status === "rejected") {
      return <div>{error.response.data}</div>;
    }

    if (status === "resolved") {
      return <PokemonCard pokemonCustom={pokemonCustom} />;
    }
  }

