      return <div>–Ø –∂–¥—É –ø–æ–∫–∞ —Ç—ã –Ω–∞—á–Ω–µ—à—å –º–µ–Ω—è –∏—Å–∫–∞—Ç—åüôà</div>;
    }
    if (status === "pending") {
      return <Loading />;
    }

    if (status === "rejected") {
      return <div>{error.response.data}</div>;
    }

    if (status === "resolved") {
      return (
        <div>
          <img
            src={pokemonCustom.data.sprites.other["official-artwork"].front_default}
            width="300px"
            alt="pokemon"
          />
