import { Component } from "react";
import axios from "axios";
import { Loading } from "components/Loading";

export class PokemonInfo extends Component {
  state = {
    pokemonCustom: null,
    loading: false,
  };
  componentDidUpdate(prevProps, _) {
    const prevName = prevProps.pokemonName;
    const currName = this.props.pokemonName;
    if (currName !== prevName) {
      this.setState({ loading: true });
      setTimeout(() => {
        axios
          .get(`https://pokeapi.co/api/v2/pokemon/${currName}`)
          .then((pokemon) => this.setState({ pokemonCustom: pokemon }))
          .catch((error) => console.log(error.message))
          .finally(() => this.setState({ loading: false }));
      }, 5000);
    }
  }

  render() {
    return (
      <div>
        {this.state.loading && <Loading />}
        {this.props.pokemonName === "" && <div>Введите имя покемона</div>}
        {this.state.pokemonCustom && <div>{this.state.pokemonCustom.data.name}</div>}
      </div>
    );
  }
}
